package com.assignmentForLoop;

public class SumOfNaturalNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int i,s=0;
    
    for(i = 1;i<=10; i++) {
    	s = s + i;
    }
    
    System.out.println(" Sum of 10 natural numbers"+s);
    
	}

}
