package operators;

public class LifewithoutLoopCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println(1);
        System.out.println(2);
	    System.out.println(3);
	    System.out.println(4);
        System.out.println(5);
        System.out.println(6);
        System.out.println(7);
        System.out.println(8); 
        System.out.println(9);
        System.out.println(10);
        
        
        // when you have to run some set of statement/code multiple time then use loop
        //now we have some condition to tell java how many times we have to iterate or execute to loop
      for(int i=1;i<=10;++i) { 
        //i have one variable whose name is i and its value is 1
    	  // please run the code till i value is either less than 10 or equal to 10
       System.out.println(i);
    }
	}
}
